// MainLayout.jsx
import { Outlet } from 'react-router-dom';
import { useState } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link } from 'react-router-dom';

const MainLayout = () => {
  const [theme, setTheme] = useState('dark');
  const handleTheme = () => setTheme(prev => (prev === 'dark' ? 'light' : 'dark'));

  return (
    <div className={`outer ${theme}`}>
      <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">&#8986; StopClock</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
            <span className="navbar-toggler-icon"></span>
          </button>

          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav ms-auto">
              <li className="nav-item"><Link className="nav-link" to="/">Home</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/about">About</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/contact">Contact</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/PrivacyandPolicy">Privacy & Policy</Link></li>
              <li className="nav-item"><Link className="nav-link" to="/TermsandConditions">Terms & Conditions</Link></li> 
            </ul>
          </div>
        </div>
      </nav>

      <button className='darkLight' onClick={handleTheme}>
        {theme === 'dark' ? <span className='circle'>üåô</span> : <span className='circle'>‚òÄÔ∏è</span>}
      </button>

      <div className="container mt-4">
        <Outlet context={{ theme, handleTheme }} />
      </div>
    </div>
  );
};

export default MainLayout;
