import { useState, useEffect } from 'react';
import './clock.css';
import 'bootstrap/dist/css/bootstrap.min.css';


function Timer() {
  const [count, setCount] = useState(0);
  const [running, setRunning] = useState(false);
  const [time, setTime] = useState({ hh: 0, mm: 0, ss: 0 });
  const [theme, setTheme] = useState('dark');

  useEffect(() => {
    let interval = null;
    if (running) {
      interval = setInterval(() => {
        setCount(prev => prev + 1);
      }, 1000);
    } else {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [running]);

  useEffect(() => {
    const hh = Math.floor(count / 3600);
    const mm = Math.floor((count % 3600) / 60);
    const ss = Math.floor(count % 60);
    setTime({ hh, mm, ss });
  }, [count]);

  const handleIntervalStart = () => setRunning(true);
  const handleIntervalPause = () => setRunning(false);
  const handleIntervalReset = () => {
    setRunning(false);
    setCount(0);
  };
  const handleTheme = () => setTheme(prev => (prev === 'dark' ? 'light' : 'dark'));

  const Time = `${String(time.hh).padStart(2, '0')} : ${String(time.mm).padStart(2, '0')} : ${String(time.ss).padStart(2, '0')}`;

  return (
    <div className={`outer ${theme}`}>
      <div className="container text-center">
        <button className="darkLight mb-3" onClick={handleTheme}>
          {theme === 'dark' ? <span className='circle'>Dark</span> : <span className='circle'>Light</span>}
        </button>

        <div className="timer my-4 shadow-sm">
          <h2 className='display-6 fw-bold'>{Time}</h2>
        </div>

        <div className="d-flex justify-content-center gap-3 mt-3">
          <button className='btn btn-primary rounded-circle px-3 py-2' onClick={handleIntervalStart}>Start</button>
          <button className='btn btn-danger rounded-circle px-3 py-2' onClick={handleIntervalPause}>Pause</button>
          <button className='btn btn-success rounded-circle px-3 py-2' onClick={handleIntervalReset}>Reset</button>
        </div>
      </div>
    </div>
  );
}

export default Timer;
